import React, {useState, useEffect} from 'react';
import {useAuth} from "../AuthContext";
import {Link, useNavigate} from 'react-router-dom';
import "./User.css";

function User() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;
    const { authToken, id } = useAuth();
    console.log(id);

    const [errorMessage, setErrorMessage] = useState('');
    // Effect to set error message based on authToken
    useEffect(() => {
        if (authToken === null) {
            setErrorMessage("You are not signed in. Please log in to get songlist.");
        } else {
            setErrorMessage(''); 
            handleSongCall();

        }
    }, [authToken]); 

    const handleSongCall= async () => {
        // call for user songs
        const formData = new FormData();
        formData.append('id', id);
        console.log('Form Data:', formData);

        try {
            const response = await axios.post(ENDPOINT + "/api/songs/usersongs", formData, {
                headers: {
                    'Content-Type': 'multipart/form-data', // Set the proper content type
                },
            });
            console.log('Upload response:', response.data);
            clearInputs();
            fileInputRef.current.value = null;
            alert('File successfully uploaded!'); 
        } catch (error) {
            console.error('Error uploading song:', error);
            alert('Error uploading file. Please try again.'); // Notify the user of the error
        }    
    }


    return (
        <>
        {!authToken && <p className="errorMessage" style={{ color: 'red' }}>{errorMessage}</p>} 
        <div>
            User Songs
        </div>
        </>
    );
}

export default User;
