import { Outlet, Link, useNavigate, useLocation } from "react-router-dom";
import { useAuth } from "../AuthContext";
import "./Layout.css";

function Layout() {
  const authContext = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = async () => {
    await authContext.logout();
    navigate("/");
  };

  // Define the paths where the music player should be visible
  const musicPlayerVisiblePaths = ["/", "/homepage"];

  // Check if the current path should show the music player
  const showMusicPlayer = musicPlayerVisiblePaths.includes(location.pathname);

  return (
    <div className="Layout-container">
      <div className="Layout">
        {/* Navigation bar */}
        <div className="nav-bar">
          <h1 className="title">MusicWebsite</h1>
          <div className="links">
            <Link className="nav-link" to="/">Home</Link>
            <Link className="nav-link" to="/community">Community</Link>
            <Link className="nav-link" to="/upload">Upload</Link>
            {authContext.authToken ? (
              <button className="nav-link emphasized" onClick={handleLogout}>Sign Out</button>
            ) : (
              <Link className="nav-link emphasized" to="/signin">Sign In</Link>
            )}
          </div>
        </div>

        {/* Main content (Outlet will render the page-specific content) */}
        <main>
          <Outlet />
        </main>
      </div>

      {/* Persistent Music Player (only shown on specific paths) */}
      {showMusicPlayer && (
        <div className="music-player">
          {/* Your music player component here */}
          <p>Music Player (Visible on selected pages)</p>
        </div>
      )}
    </div>
  );
}

export default Layout;
