import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './MusicPlayer.css';  // Import the CSS for styling

const MusicPlayer = ({ songId }) => {
    const [isPlaying, setIsPlaying] = useState(false);
    const [audioUrl, setAudioUrl] = useState(null);
    const audioRef = useRef(null);
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;

    // Fetch the song's streaming URL when the songId changes
    useEffect(() => {
        const fetchSongStream = async () => {
            try {
                const response = await axios.get(ENDPOINT + `/api/songs/streamSongById`, {
                    params: { songId },
                    responseType: 'blob'  // Expect binary data from the backend
                });

                // Cancel any previous requests (stop the previous song)
                if (audioRef.current) {
                    audioRef.current.pause();
                    audioRef.current.currentTime = 0;  // Reset to start of song
                }

                // Create a new audio URL for the new song stream
                const audioBlob = response.data;
                const audioUrl = URL.createObjectURL(audioBlob);
                setAudioUrl(audioUrl); // Set the audio URL to play the song
            } catch (error) {
                console.error('Error fetching song stream:', error);
            }
        };

        if (songId) {
            fetchSongStream();
        }
    }, [songId]);  // Re-fetch when songId changes

    useEffect(() => {
        // When the song URL is ready, start playing the song
        if (audioRef.current && audioUrl) {
            audioRef.current.load();  // Make sure the audio is loaded before playing
            audioRef.current.play().catch((error) => {
                console.error('Error trying to play audio:', error);
            });
        }
    }, [audioUrl]);  // Trigger play whenever the song URL changes

    const togglePlayPause = () => {
        const audio = audioRef.current;
        if (isPlaying) {
            audio.pause();
        } else {
            audio.play().catch((error) => {
                console.error('Error trying to play audio:', error);
            });
        }
        setIsPlaying(!isPlaying);
    };

    return (
        <div className="music-player">
            {audioUrl ? (
                <>
                    <audio ref={audioRef} src={audioUrl} />
                    <div className="button-container">
                        <div className={isPlaying ? 'square' : 'triangle'} onClick={togglePlayPause}></div>
                    </div>
                </>
            ) : (
                <p>Select a song to play</p>
            )}
        </div>
    );
};

export default MusicPlayer;
