import React, {useState, useEffect} from 'react';
import {useAuth} from "../AuthContext";
import {Link, useNavigate} from 'react-router-dom';
import "./User.css";

function User() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;
    const { authToken, id } = useAuth();
    console.log(id);

    const [errorMessage, setErrorMessage] = useState('');
    // Effect to set error message based on authToken
    useEffect(() => {
        if (authToken === null) {
            setErrorMessage("You are not signed in. Please log in to get songlist.");
        } else {
            setErrorMessage(''); 
        }
    }, [authToken]); 

    if(authToken)
    {
        const formData = new FormData();
        formData.append('id', id);
    }

    return (
        <>
        {!authToken && <p className="errorMessage" style={{ color: 'red' }}>{errorMessage}</p>} 
        <div>
            User Songs
        </div>
        </>
    );
}

export default User;
