import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MusicPlayer from '../MusicPlayer/MusicPlayer'; 
import './HomePage.css';

function HomePage() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;

    const [songs, setSongs] = useState([]);
    const [loading, setLoading] = useState(true);
    const [currentSong, setCurrentSong] = useState(null);

    useEffect(() => {
        const fetchSongs = async () => {
            try {
                const response = await axios.post(ENDPOINT + '/api/songs/homepagesongs');
                if (response.data.message === 'Success') {
                    setSongs(response.data.songs);
                } else {
                    console.log('No songs found.');
                }
            } catch (error) {
                console.error('Error fetching songs:', error);
            } finally {
                setLoading(false);
            }
        };

        fetchSongs();
    }, [ENDPOINT]);

    const handlePlaySong = async (songId) => {
        try {
            const response = await axios.get(ENDPOINT + '/api/songs/streamSongById', {
                params: { songId },
                responseType: 'blob',
            });

            const audioUrl = URL.createObjectURL(response.data);
            setCurrentSong(audioUrl); // Pass the song URL to the MusicPlayer
        } catch (error) {
            console.error('Error streaming song:', error);
        }
    };

    if (loading) {
        return <p>Loading songs...</p>;
    }

    return (
        <div className="homepage">
            <div className="song-list">
                {songs.map((song) => (
                    <div className="song-block" key={song.songId}>
                        <h2>{song.title}</h2>
                        <p>{song.description}</p>
                        <button onClick={() => handlePlaySong(song.streamId)}>Play</button>
                    </div>
                ))}
            </div>
            <MusicPlayer songUrl={currentSong} />
        </div>
    );
}

export default HomePage;
