import React, {useState, useEffect} from 'react';
import {useAuth} from "../AuthContext";
import axios from "axios";
import {Link, useNavigate} from 'react-router-dom';
import "./HomePage.css";

function HomePage() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;

    // error messages and songs
    const [songs, setSongs] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const handleSongCall = async () => {
            try {
                const response = await axios.post(ENDPOINT + "/api/songs/homepagesongs");
                if (response.data.message === "Success") {
                    console.log("Songs retrieved successfully:", response.data.songs);
                    setSongs(response.data.songs);
                } else {
                    console.log("No songs found for this user");
                }
            } catch (error) {
                console.error("Error retrieving songs:", error);
            } finally {
                setLoading(false); // Set loading to false once the request is complete
            }
        };

        handleSongCall();
    }, [ENDPOINT]);

    const streamSongById = async (songId) => {
        try {
            // Make GET request with songId as query parameter
            const response = await axios.get(`/api/songs/streamSongById`, {
                params: { songId },
                headers: {
                },
                responseType: 'blob', 
            });

            // Get the song's metadata from response headers
            const songTitle = response.headers['x-song-title'];
            const songDescription = response.headers['x-song-description'];

            console.log('Song Title:', songTitle);
            console.log('Song Description:', songDescription);

            // Create a URL for the Blob (audio file)
            const audioUrl = URL.createObjectURL(response.data);

            // Create an audio element to play the song
            const audioElement = new Audio(audioUrl);
            audioElement.play();
        } catch (error) {
            console.error('Error streaming song:', error);
        }
    };

    // Show loading state while songs are being fetched
    if (loading) {
        return <p>Loading songs...</p>;
    }

    return (
        <div className="song-list">
            {songs.length > 0 ? (
                songs.map((song) => (
                    <div className="song-block" key={song.songId}>
                        <h2 className="song-title">{song.title}</h2>
                        <p className="song-description">{song.description}</p>
                        <button
                            className="play-button"
                            onClick={() => streamSongById(song.songId)} // Play the song when clicked
                        >
                            Play
                        </button>
                    </div>
                ))
            ) : (
                <p>No songs to display</p>
            )}
        </div>
    );
}

export default HomePage;
