import React, {useState, useEffect} from 'react';
import {useAuth} from "../AuthContext";
import axios from "axios";
import {Link, useNavigate} from 'react-router-dom';
import "./User.css";

function User() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;
    const { authToken, id } = useAuth();
    console.log(id);

    const requestData = {
        id: id
    };

    const [errorMessage, setErrorMessage] = useState('');
    // Effect to set error message based on authToken
    useEffect(() => {
        if (authToken === null) {
            setErrorMessage("You are not signed in. Please log in to get songlist.");
        } else {
            setErrorMessage(''); 
            handleSongCall();

        }
    }, [authToken]); 

    const handleSongCall = async () => {
        axios.post(ENDPOINT + "/api/songs/usersongs", requestData)
        .then(response => {
            if (response.data.message === "Success") {
                console.log("Songs retrieved successfully:", response.data.songs);
            } else {
                console.log("No songs found for this user");
            }
        })
        .catch(error => {
            console.error("Error retrieving songs:", error);
        });
    }


    return (
        <>
        {!authToken && <p className="errorMessage" style={{ color: 'red' }}>{errorMessage}</p>} 
        <div>
        <ul>
            {songs.map((song) => (
                <li key={song.songId}>
                    <h2>{song.title}</h2>
                    <p>{song.description}</p>
                </li>
            ))}
        </ul>
        </div>
        </>
    );
}

export default User;
