import React, { useState, useEffect } from 'react';
import { useAuth } from "../AuthContext";
import axios from "axios";
import { Link, useNavigate } from 'react-router-dom';
import MusicPlayer from "../MusicPlayer/MusicPlayer"; // Import the MusicPlayer component
import "./User.css";

function User() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;
    const { authToken, id } = useAuth();
    const navigate = useNavigate(); 
    const [userInfo, setUserInfo] = useState(null);
    const [error, setError] = useState(null);


    const requestData = {
        id: id
    };

    // error messages and songs
    const [errorMessage, setErrorMessage] = useState('');
    const [songs, setSongs] = useState([]);
    const [currentSongId, setCurrentSongId] = useState(null); // To store the song ID for playing

    // Effect to set error message based on authToken
    useEffect(() => {
        if (authToken === null) {
            setErrorMessage("You are not signed in. Please log in to get songlist.");
        } else {
            setErrorMessage('');
            handleSongCall();
            UserProfile();
        }
    }, [authToken]);

    const handleSongCall = async () => {
        try {
            const response = await axios.post(ENDPOINT + "/api/songs/usersongs", requestData);
            if (response.data.message === "Success") {
                console.log("Songs retrieved successfully:", response.data.songs);
                setSongs(response.data.songs);
            } else {
                console.log("No songs found for this user");
            }
        } catch (error) {
            console.error("Error retrieving songs:", error);
        }
    };

    const UserProfile = async () => {
        try {
            const response = await axios.get(`/api/users/read/${id}`);
            if (response.status === 200) {
                setUserInfo(response.data.userInfo);
                console.log(userInfo);
            }
        } catch (error) {
            if (error.response) {
                // If the response has an error status code
                setError(error.response.data.message);
            } else {
                setError("Error fetching user profile");
            }
        }
    };



    return (
        <div>
            {!authToken && <p className="errorMessage" style={{ color: 'red' }}>{errorMessage}</p>}

            <h1>Song List</h1>
            <div className="song-list">
                {songs.length > 0 ? (
                    songs.map((song) => (
                        <div className="song-block" key={song.songId}>
                            <h2>{song.title}</h2>
                            <p>{song.description}</p>
                            <button onClick={() => setCurrentSongId(song.streamId)}>Play</button>
                        </div>
                    ))
                ) : (
                    <p>No songs to display</p>
                )}
            </div>

            {/* MusicPlayer Component */}
            {currentSongId && <MusicPlayer songId={currentSongId} />}
        </div>
    );
}

export default User;
