import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import './MusicPlayer.css';  // Import the CSS for styling

const MusicPlayer = ({ songId }) => {
    const [isPlaying, setIsPlaying] = useState(false);
    const [currentSongUrl, setCurrentSongUrl] = useState(null);
    const audioRef = useRef(null);

    // Fetch the song's streaming URL when the songId changes
    useEffect(() => {
        const fetchSongStream = async () => {
            try {
                const response = await axios.get(`/api/songs/streamSongById`, {
                    params: { songId }
                });
                // Create a blob URL for the song stream
                const audioUrl = URL.createObjectURL(response.data);
                setCurrentSongUrl(audioUrl); // Set the URL to play the song
            } catch (error) {
                console.error('Error fetching song stream:', error);
            }
        };

        if (songId) {
            fetchSongStream();
        }
    }, [songId]);

    useEffect(() => {
        // When the song URL is ready, start playing the song
        if (audioRef.current && currentSongUrl) {
            audioRef.current.load();
            audioRef.current.play();
        }
    }, [currentSongUrl]); // Trigger play whenever the song URL changes

    const togglePlayPause = () => {
        const audio = audioRef.current;
        if (isPlaying) {
            audio.pause();
        } else {
            audio.play();
        }
        setIsPlaying(!isPlaying);
    };

    return (
        <div className="music-player">
            {currentSongUrl ? (
                <>
                    <audio ref={audioRef} src={currentSongUrl} />
                    <div className="button-container">
                        <div className={isPlaying ? 'square' : 'triangle'} onClick={togglePlayPause}></div>
                    </div>
                </>
            ) : (
                <p>Select a song to play</p>
            )}
        </div>
    );
};

export default MusicPlayer;
