import React, {useState, useEffect} from 'react';
import {useAuth} from "../AuthContext";
import axios from "axios";
import {Link, useNavigate} from 'react-router-dom';
import "./HomePage.css";

function HomePage() {
    const ENDPOINT = process.env.REACT_APP_API_ENDPOINT;
    const { authToken, id } = useAuth();
    console.log(id);

    const requestData = {
        id: id
    };

    // error messages and songs
    const [errorMessage, setErrorMessage] = useState('');
    const [songs, setSongs] = useState([]);

    // Effect to set error message based on authToken
    useEffect(() => {
        if (authToken === null) {
            setErrorMessage("You are not signed in. Please log in to get songlist.");
        } else {
            setErrorMessage(''); 
            handleSongCall();

        }
    }, [authToken]); 

    const handleSongCall = async () => {
        axios.post(ENDPOINT + "/api/songs/usersongs", requestData)
        .then(response => {
            if (response.data.message === "Success") {
                console.log("Songs retrieved successfully:", response.data.songs);
                setSongs(response.data.songs);
            } else {
                console.log("No songs found for this user");
            }
        })
        .catch(error => {
            console.error("Error retrieving songs:", error);
        });
    }


    return (
        <>
            {!authToken && <p className="errorMessage" style={{ color: 'red' }}>{errorMessage}</p>}
            <div className="song-list">
                {songs.length > 0 ? (
                    songs.map((song) => (
                        <div className="song-block" key={song.songId}>
                            <h2 className="song-title">{song.title}</h2>
                            <p className="song-description">{song.description}</p>
                            <button className="play-button">Play</button>
                        </div>
                    ))
                ) : (
                    <p>No songs to display</p>
                )}
            </div>
        </>
    );
}

export default HomePage;
